function LicenseManager(context) {
    var licenseCollection, encodedTopLicense, response, topLicense, xmlDoc, $xml, licenseType, licenseIsValid, licenseIsTest, a;



    LicenseManager.prototype.checkLicenseOnline = function () {
        //Retrieve license from SharePoint; change this productId with the one for your app; you can get it from AppManifest.xml
        //Replace this GUID with the apps GUID
        EM.addStatusMessage('Starting license check...');
        licenseCollection = SP.Utilities.Utility.getAppLicenseInformation(context, '{261a6492-e332-4b92-87e3-3519c223f329}');
        context.executeQueryAsync(onRetrieveLicenseFromSPSuccess, onRetrieveLicenseFromSPFailure);
    };

    //Retrieval call to license succeded (doesn't mean there is a license, look at the contents to see if there is one)
    function onRetrieveLicenseFromSPSuccess() {

        if (licenseCollection.get_count() > 0) {
            topLicense = licenseCollection.get_item(0).get_rawXMLLicenseToken();

            //debug
            EM.addStatusMessage('License retrieved from SharePoint: ' + topLicense);

            //encode license; required to call the verification service since it will be sent on the URL 
            encodedTopLicense = encodeURIComponent(topLicense);

            //debug, display the encoded license 
            EM.addStatusMessage('License ready to be verified:' + encodedTopLicense);
        }
        else {
            EM.addStatusMessage('The user doesnt have a license');
        }


        //Call verification service via the WebProxy; this ensures the validity of the license in case SharePoint was tampered with
        var request = new SP.WebRequestInfo();
        //We use the REST flavor of the verification service
        request.set_url('https://verificationservice.officeapps.live.com/ova/verificationagent.svc/rest/verify?token=' + encodedTopLicense);
        request.set_method('GET');
        response = SP.WebProxy.invoke(context, request);
        // Set the event handlers and invoke the request
        context.executeQueryAsync(onVerificationCallSuccess, onVerificationCallFailure);

    };

    LicenseManager.prototype.al = function () {
        return a;
    };

    // This function is executed if the above call fails
    function onRetrieveLicenseFromSPFailure(sender, args) {
        EM.addErrorMessage('Failed to retrieve license:' + args.get_message());
    };

    function onVerificationCallSuccess() {
        var stories;
        var storiesHTML;
        var xmlDOM;

        //It is highly recommended to cache (e.g. cookies) the response
        //If you are concerned about tampering only re-retrieve and re-validate the license for sensitive operations in your App. 
        var verificationResponse = response.get_body();


        //Debug, display the raw response
        EM.addStatusMessage('Verification Service Response' + verificationResponse);

        xmlDoc = $.parseXML(verificationResponse);
        $xml = $(xmlDoc);
        licenseType = $xml.find('EntitlementType').text();
        licenseIsValid = $xml.find('IsValid').text();
        licenseIsTest = $xml.find('IsTest').text();

        EM.addStatusMessage('License type ' + licenseType);
        //DO SOMETHING NOW THAT YOU KNOW WHAT TYPE OF LICENSE THE USER HAS
        a = false;
        switch (licenseType) {
            case 'Free':
                $('#unlicensedmessage').css('display', 'block');
                PM.setParameter('license', createLicenseString(false));
                break;
            case 'Paid':
                PM.setParameter('license', createLicenseString(true));
             //   setTimeout(function () { window.location.reload(); }, 5000);
                a = true;
                break;
            case 'Trial':
                $('#unlicensedmessage').css('display', 'block');
                PM.setParameter('license', createLicenseString(false));
                //You can then look at the expiration date on the response
                break;
            default:
                $('#unlicensedmessage').css('display', 'block');
                PM.setParameter('license', createLicenseString(false));
                break;
        }


    };

    function onVerificationCallFailure() {
        EM.addErrorMessage('Failed to call verification service via WebProxy:' + args.get_message());
    };

    function createLicenseString(licensed) {
        var l = Math.floor(Math.random() * 10 + 10);
        var licensestring = PM.getQueryStringParameter('SPHostUrl');
        var nextLicenseCheck = new Date();
        //check the license tomorrow next time
        nextLicenseCheck.setDate(nextLicenseCheck.getDate() + 1);
        if (!licensed) licensestring = 'UNLICENSED:' + nextLicenseCheck.toUTCString();
        /*      for (var i = 0; i < l; i++) {
                  if (Math.random() > 0.5) licensestring += Math.floor(Math.random() * 8);
                  else licensestring = Math.floor(Math.random() * 8) + licensestring;
              }*/
        var encoder = new Encoder();
        encoder.initAlphabet(1001);
        var coded = encoder.code(licensestring);
        /*   encoder.initAlphabet(1001);
           var decoded = encoder.decode(coded);*/
        return coded;
    };

    LicenseManager.prototype.verifyLicense = function (license) {
        var host = PM.getQueryStringParameter('SPHostUrl');
        var encoder = new Encoder();
        encoder.initAlphabet(1001);
        var decoded = encoder.decode(license);
        a = false;
        if (decoded.indexOf(host) > -1) {
            $('#unlicensedmessage').css('display', 'none');
            EM.addStatusMessage('License found and is OK and aligned with host: ' + host);
            a = true;
            return true;
        }
        else if (decoded.indexOf('UNLICENSED') == -1) {
            EM.addStatusMessage('License has not been set');
            $('#unlicensedmessage').css('display', 'block');
            LicenseManager.prototype.checkLicenseOnline(null);
            return false;
        }
        else {
            $('#unlicensedmessage').css('display', 'block');
            var nextLicenseCheck = new Date(decoded.substring(11));
            if (nextLicenseCheck < new Date()) LicenseManager.prototype.checkLicenseOnline(null);
            else EM.addStatusMessage('License will be checked next time ' + nextLicenseCheck.toString());
            return false;
        }
    };

};

